import React, { useEffect, useState } from 'react'
import { ChatElement } from './ChatElement'
import { ref, push, getDatabase, onValue, remove, set } from "firebase/database";
import app from '../firebaseConfig';

interface todo{
    id:string
}
interface Message{
    id:string
    createdAt:string
    text:string
    uid:string
    userPic:string
}
const Home = ({authUser}:any) => {
    const db = getDatabase(app);
    const chatRef = ref(db, "/messages");
    const [conversation,setConversation]=useState<Message[]>([])
    useEffect(() => {

        const todoRef = ref(db, "/messages");
        onValue(todoRef, (snapshot) => {
            const todos = snapshot.val();
            let newChats:Message[] = [];
            console.log(todos)
            for (let id in todos) {
                newChats.push({ id, ...todos[id] });
            };
            setTimeout(() => {
                setConversation(newChats)
            }, 5)

        })
    }, []);

const Sender=(e:any)=>{
e.preventDefault()
e.target.message
}

  return (
  <>
  <div className="col-12 d-flex flex-column justify-content-between">
<div className="container col-12 border my-3 p-2 chatBox">
<ChatElement pic={authUser.photoURL} align={(authUser.uid==='YKGyoIDGWgXpCayrJ1ZCjsCXQAG3')?'msg-right':'msg-left'}/>
</div>
  <form className="input-group mb-3" onSubmit={Sender} style={{height:'8vh'}}>
  <input type="text" className="form-control" name="message" placeholder="messaging..." aria-label="Recipient's username" aria-describedby="button-addon2"/>
  <button className="btn btn-primary" type="submit" id="button-addon2">Send</button>
</form>
  </div>
  </>
  )
}

export default Home
