import React, { useEffect } from 'react'
import { ChatElement } from './ChatElement'
import { ref, push, getDatabase, onValue, remove, set } from "firebase/database";
import app from '../firebaseConfig';

const Home = ({authUser}:any) => {
    const db = getDatabase(app);
    const chatRef = ref(db, "/messages");

    useEffect(() => {

        const todoRef = ref(db, "/todos");
        onValue(todoRef, (snapshot) => {
            const todos = snapshot.val();
            const newChats = [];
            for (let id in todos) {
                newChats.push({ id, ...todos[id] });
            };
            setTimeout(() => {
              setData(newChats)
            }, 5)

        })
    }, []);



  return (
  <>
  <div className="col-12 d-flex flex-column justify-content-between">
<div className="container col-12 border my-3 p-2 chatBox">
<ChatElement pic={authUser.photoURL} align={(authUser.uid==='YKGyoIDGWgXpCayrJ1ZCjsCXQAG3')?'msg-right':'msg-left'}/>
</div>
  <form className="input-group mb-3" style={{height:'8vh'}}>
  <input type="text" className="form-control" placeholder="messaging..." aria-label="Recipient's username" aria-describedby="button-addon2"/>
  <button className="btn btn-primary" type="button" id="button-addon2">Send</button>
</form>
  </div>
  </>
  )
}

export default Home
