import app from './firebaseConfig';
function App() {
 const auth=getAuth(app)
 const [authUser,setAuthUser]=useState(null)
useEffect(()=>{
const listen =onAuthStateChanged(auth,(user:any)=>{
if(user){
  setAuthUser(user)
}else{
  setAuthUser(null)
}
})
},[])


  return (
    <>

    <SignIn/>
      <NavBar />
      <div className="container d-flex">
        <Home />
      </div>
    </>
  );
}

export default App;
